<?phprequire_once 'db.php';$query = $_GET['query'];$sql = sprintf("SELECT DISTINCT industry FROM interviews where industry LIKE '%s' AND schedule_date>='%s' AND active='%s' AND del_status='%s'", '%' . $query . '%',date('Y-m-d'),1,0);$result = Db::query($sql);$data = array();while ($row = mysql_fetch_array($result)) {    array_push($data, $row['industry']);}$sql = sprintf("SELECT DISTINCT job_listing FROM jobs where jobs.job_listing LIKE '%s' AND closing_date>='%s' AND active='%s' AND del_status='%s'", '%' . $query . '%',date('Y-m-d'),1,0);$result = Db::query($sql);$data = array();while ($row = mysql_fetch_array($result)) {    array_push($data, $row['job_listing']);}echo json_encode($data);